#include "Packet/StaticData/Building/BuildingDocLookup.h"

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"
#include "Packet/StaticDataAccessor/StaticDataAccessor.h"
#include "Packet/StaticDataCatalog/StaticDataCatalog.h"
#include "Packet/StaticData/Building/BuildingDoc.h"


BuildingDocLookup::~BuildingDocLookup()
{
	Clear();
}
void BuildingDocLookup::Clear()
{	
	for (auto& [_, doc] : mBuildingDocMap)
	{
		_DELETE(doc);
	}
	mBuildingDocMap.clear();
}

void BuildingDocLookup::Load()
{
	for (const auto& it : gStaticDataCatalog->GetList<STATIC_BUILDING>())
	{
		auto key = it->Get_c_building_item_sid();
		auto found = mBuildingDocMap.find(key);
		BuildingDoc* doc = (mBuildingDocMap.end() != found) ?
			found->second : _NEW<BuildingDoc>();
		doc->mStaticBuilding = *it;
		mBuildingDocMap.emplace(key, doc);

		doc->mStaticBuildingLevelList.clear();
	}
}

void BuildingDocLookup::Link()
{
}

const BuildingDoc* BuildingDocLookup::Find(ItemSid buildingItemSid) const
{
	const auto it = mBuildingDocMap.find(buildingItemSid);

	if (mBuildingDocMap.end() == it)
	{
		_DEBUG_BREAK;
		_DEBUG_RED;
		return nullptr;
	}

	return it->second;
}
