#include "Packet/StaticData/CharacterSkill/CharacterSkillDocLookup.h"

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"
#include "Packet/StaticDataAccessor/StaticDataAccessor.h"
#include "Packet/StaticDataCatalog/StaticDataCatalog.h"
#include "Packet/StaticData/CharacterSkill/CharacterSkillDoc.h"


CharacterSkillDocLookup::~CharacterSkillDocLookup()
{
	Clear();
}
void CharacterSkillDocLookup::Clear()
{
	for (auto& it : mCharacterSkillDocMap)
	{
		CharacterSkillDoc* doc = it.second;

		_DELETE(doc);
	}
	mCharacterSkillDocMap.clear();
}

void CharacterSkillDocLookup::Load()
{
	for (auto& it : gStaticDataCatalog->GetList<STATIC_CHARACTER_SKILL>())
	{
		auto key = it->Get_c_character_item_sid();
		auto found = mCharacterSkillDocMap.find(key);
		CharacterSkillDoc* doc = (mCharacterSkillDocMap.end() != found) ?
			found->second : _NEW<CharacterSkillDoc>();
		doc->mStaticCharacterSkillList.emplace_back(*it);
		mCharacterSkillDocMap.emplace(key, doc);
	}
}

void CharacterSkillDocLookup::Link()
{
}

const CharacterSkillDoc* CharacterSkillDocLookup::Find(ItemSid characterItemSid) const
{
	const auto it = mCharacterSkillDocMap.find(characterItemSid);

	if (mCharacterSkillDocMap.end() == it)
	{
		//_DEBUG_BREAK;
		//_DEBUG_RED;
		return nullptr;
	}

	return it->second;
}
