#include "Packet/StaticData/Item/ItemDocLookup.h"

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"
#include "Packet/StaticDataAccessor/StaticDataAccessor.h"
#include "Packet/StaticDataCatalog/StaticDataCatalog.h"
#include "Packet/StaticData/Item/ItemDoc.h"
#include "Packet/StaticData/ItemStat/ItemStatDocLookup.h"


ItemDocLookup::~ItemDocLookup()
{
	Clear();
}
void ItemDocLookup::Clear()
{
	for (auto& [_, doc] : mItemDocMap)
	{
		_DELETE(doc);
	}
	mItemDocMap.clear();
}

void ItemDocLookup::Load()
{
	for (const auto& it : gStaticDataCatalog->GetList<STATIC_ITEM>())
	{
		auto key = it->Get_c_item_sid();
		auto found = mItemDocMap.find(key);
		ItemDoc* doc = (mItemDocMap.end() != found) ? found->second : _NEW<ItemDoc>();
		doc->mStaticItem = *it;
		mItemDocMap.emplace(key, doc);
	}
}

void ItemDocLookup::Link()
{
	{
		const std::shared_ptr<ItemStatDocLookup> view = gStaticDataAccessor->Get<ItemStatDocLookup>();
		for (auto& [sid, doc] : mItemDocMap)
			doc->mItemStatDoc = view->Find(sid);
	}
}

const ItemDoc* ItemDocLookup::Find(ItemSid itemSid) const
{
	const auto it = mItemDocMap.find(itemSid);

	if (mItemDocMap.end() == it)
	{
		_DEBUG_BREAK;
		_DEBUG_RED;
		return nullptr;
	}

	return it->second;
}
