#include "Packet/StaticData/Map/MapDocLookup.h"

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"
#include "Packet/StaticDataAccessor/StaticDataAccessor.h"
#include "Packet/StaticDataCatalog/StaticDataCatalog.h"
#include "Packet/StaticData/Map/MapDoc.h"


MapDocLookup::~MapDocLookup()
{
	Clear();
}
void MapDocLookup::Clear()
{
	for (auto& [_, doc] : mMapDocMap)
	{
		_DELETE(doc);
	}
	mMapDocMap.clear();
}

void MapDocLookup::Load()
{
	for (auto& it : gStaticDataCatalog->GetList<STATIC_MAP>())
	{
		auto key = it->Get_c_map_sid();
		auto found = mMapDocMap.find(key);
		MapDoc* doc = (mMapDocMap.end() != found) ? found->second : _NEW<MapDoc>();
		doc->mStaticMap = *it;
		mMapDocMap.emplace(key, doc);
	}
}

void MapDocLookup::Link()
{

}

const MapDoc* MapDocLookup::Find(MapSid mapSid) const
{
	const auto it = mMapDocMap.find(mapSid);

	if (mMapDocMap.end() == it)
	{
		_DEBUG_BREAK;
		_DEBUG_RED;
		return nullptr;
	}

	return it->second;
}
