#include "Packet/StaticData/Mission/MissionDocLookup.h"

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"
#include "Packet/StaticDataAccessor/StaticDataAccessor.h"
#include "Packet/StaticDataCatalog/StaticDataCatalog.h"
#include "Packet/StaticData/Mission/MissionDoc.h"


MissionDocLookup::~MissionDocLookup()
{
	Clear();
}
void MissionDocLookup::Clear()
{	
	for (auto& [_, doc] : mMissionDocMap)
	{
		_DELETE(doc);
	}
	mMissionDocMap.clear();
}

void MissionDocLookup::Load()
{
	for (const auto& it : gStaticDataCatalog->GetList<STATIC_MISSION>())
	{
		auto key = it->Get_c_mission_sid();
		auto found = mMissionDocMap.find(key);
		MissionDoc* doc = 
			(mMissionDocMap.end() != found) ? found->second : _NEW<MissionDoc>();
		doc->mStaticMission = *it;
		mMissionDocMap.emplace(key, doc);
	}
}

void MissionDocLookup::Link()
{
}

const MissionDoc* MissionDocLookup::Find(MissionSid missionSid) const
{
	const auto it = mMissionDocMap.find(missionSid);

	if (mMissionDocMap.end() == it)
	{
		_DEBUG_BREAK;
		_DEBUG_RED;
		return nullptr;
	}

	return it->second;
}
