#pragma pack(pop)

inline bool ValidatePacketCG(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::CG_PACKET_END - PacketTypes::CG_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<CG_WORLD_USER_COMMAND>,
       &ValidatePacketFunc<CG_REWIND_WORLD_DATA>
   };

   if ((PacketTypes::CG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::CG_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::CG_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketCG(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::CG_PACKET_END - PacketTypes::CG_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, CG_WORLD_USER_COMMAND, _Args&&...>,
       &DispatchPacketFunc<_Owner, CG_REWIND_WORLD_DATA, _Args&&...>
   };

   if ((PacketTypes::CG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::CG_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::CG_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

