class DF_ACK_USER_LOGIN;

class DF_ACK_USER_LOGIN_WRITER : public NetworkPacketWriter
{
public:
   static constexpr PacketType PACKET_TYPE = PacketTypes::DF_ACK_USER_LOGIN;

public:
   // (4)
   explicit DF_ACK_USER_LOGIN_WRITER(PacketTraitAck trait, uint8_t* buf, const NetworkPacket& rp, Result packetResult)
       :
       NetworkPacketWriter(trait, PacketTypes::DF_ACK_USER_LOGIN, buf)
   {
       SetReadPacket(rp);
       GetHeader().SetPacketResult(packetResult);
   }

   // (5)
   explicit DF_ACK_USER_LOGIN_WRITER(PacketTraitAck trait, uint8_t* buf, const NetworkPacketWriter& wp, Result packetResult)
       :
       NetworkPacketWriter(trait, PacketTypes::DF_ACK_USER_LOGIN, buf)
   {
       SetWritePacket(wp);
       GetHeader().SetPacketResult(packetResult);
   }

   // (6)
   explicit DF_ACK_USER_LOGIN_WRITER(PacketTraitAck trait, uint8_t* buf, const PacketHeader& header, Result packetResult)
       :
       NetworkPacketWriter(trait, PacketTypes::DF_ACK_USER_LOGIN, buf)
   {
       SetHeader(header);
       GetHeader().SetPacketResult(packetResult);
   }

   // (8)
   explicit DF_ACK_USER_LOGIN_WRITER(uint8_t* buf)
       :
       NetworkPacketWriter(buf) 
   {
   }

   void SetValues(
       const ACCOUNT_USER& accountUser,
       const USER& user,
       Checksum staticDataChecksum
   )
   {
       PacketSize& accountUserOffset = *(PacketSize*)GetPacketBufWritingPtr();
       WriteValue(PacketSize(0));
       PacketSize& userOffset = *(PacketSize*)GetPacketBufWritingPtr();
       WriteValue(PacketSize(0));
       WriteValue(staticDataChecksum);


       accountUserOffset = GetPacketSize();
       WriteBinary((uint8_t*)&accountUser, accountUser.GetPacketSize());

       userOffset = GetPacketSize();
       WriteBinary((uint8_t*)&user, user.GetPacketSize());

   }

   void SetValues(
       const ACCOUNT_USER::Writer& accountUser,
       const USER::Writer& user,
       Checksum staticDataChecksum
   )
   {
       PacketSize& accountUserOffset = *(PacketSize*)GetPacketBufWritingPtr();
       WriteValue(PacketSize(0));
       PacketSize& userOffset = *(PacketSize*)GetPacketBufWritingPtr();
       WriteValue(PacketSize(0));
       WriteValue(staticDataChecksum);


       accountUserOffset = GetPacketSize();
       WriteBinary(accountUser.GetPacketBufPtr(), accountUser.GetPacketSize());

       userOffset = GetPacketSize();
       WriteBinary(user.GetPacketBufPtr(), user.GetPacketSize());

   }

   DF_ACK_USER_LOGIN& GetPacket() const noexcept { return *reinterpret_cast<DF_ACK_USER_LOGIN*>(GetPacketBufPtr()); }
   DF_ACK_USER_LOGIN* GetPacketPtr() const noexcept { return reinterpret_cast<DF_ACK_USER_LOGIN*>(GetPacketBufPtr()); }

};


