#pragma pack(pop)

inline bool ValidatePacketDF(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::DF_PACKET_END - PacketTypes::DF_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<DF_ACK_DC_BYPASS>,
       &ValidatePacketFunc<DF_ACK_USER_LOGIN>,
       &ValidatePacketFunc<DF_USER_DISCONNECT>
   };

   if ((PacketTypes::DF_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::DF_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::DF_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketDF(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::DF_PACKET_END - PacketTypes::DF_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, DF_ACK_DC_BYPASS, _Args&&...>,
       &DispatchPacketFunc<_Owner, DF_ACK_USER_LOGIN, _Args&&...>,
       &DispatchPacketFunc<_Owner, DF_USER_DISCONNECT, _Args&&...>
   };

   if ((PacketTypes::DF_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::DF_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::DF_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

