#pragma pack(pop)

inline bool ValidatePacketDM(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::DM_PACKET_END - PacketTypes::DM_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<DM>,
       &ValidatePacketFunc<DM_ACK_AUTH_TICKET>,
       &ValidatePacketFunc<DM_REQ_GAME_CREATE>,
       &ValidatePacketFunc<DM_REQ_GAME_USER_ENTER>,
       &ValidatePacketFunc<DM_REQ_GAME_USER_LEAVE>,
       &ValidatePacketFunc<DM_REQ_GAME_FINISH>
   };

   if ((PacketTypes::DM_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::DM_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::DM_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketDM(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::DM_PACKET_END - PacketTypes::DM_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, DM, _Args&&...>,
       &DispatchPacketFunc<_Owner, DM_ACK_AUTH_TICKET, _Args&&...>,
       &DispatchPacketFunc<_Owner, DM_REQ_GAME_CREATE, _Args&&...>,
       &DispatchPacketFunc<_Owner, DM_REQ_GAME_USER_ENTER, _Args&&...>,
       &DispatchPacketFunc<_Owner, DM_REQ_GAME_USER_LEAVE, _Args&&...>,
       &DispatchPacketFunc<_Owner, DM_REQ_GAME_FINISH, _Args&&...>
   };

   if ((PacketTypes::DM_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::DM_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::DM_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

