#pragma pack(pop)

inline bool ValidatePacketFG(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::FG_PACKET_END - PacketTypes::FG_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<FG_CG_BYPASS>,
       &ValidatePacketFunc<FG_REQ_GAME_CHANNEL_USER_ENTER>
   };

   if ((PacketTypes::FG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::FG_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::FG_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketFG(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::FG_PACKET_END - PacketTypes::FG_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, FG_CG_BYPASS, _Args&&...>,
       &DispatchPacketFunc<_Owner, FG_REQ_GAME_CHANNEL_USER_ENTER, _Args&&...>
   };

   if ((PacketTypes::FG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::FG_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::FG_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

