inline bool ValidatePacketGD(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::GD_PACKET_END - PacketTypes::GD_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<GD_REQ_STATIC_DATA_CHECKSUM>,
       &ValidatePacketFunc<GD_REQ_STATIC_DATA>,
       &ValidatePacketFunc<GD_ACK_ITEM_USE>,
       &ValidatePacketFunc<GD_ACK_GAME_USER_DATA_LOAD>,
       &ValidatePacketFunc<GD_GAME_USER_ITEM_UPDATE>,
       &ValidatePacketFunc<GD_REQ_GAME_FINISH>
   };

   if ((PacketTypes::GD_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::GD_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::GD_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketGD(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::GD_PACKET_END - PacketTypes::GD_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, GD_REQ_STATIC_DATA_CHECKSUM, _Args&&...>,
       &DispatchPacketFunc<_Owner, GD_REQ_STATIC_DATA, _Args&&...>,
       &DispatchPacketFunc<_Owner, GD_ACK_ITEM_USE, _Args&&...>,
       &DispatchPacketFunc<_Owner, GD_ACK_GAME_USER_DATA_LOAD, _Args&&...>,
       &DispatchPacketFunc<_Owner, GD_GAME_USER_ITEM_UPDATE, _Args&&...>,
       &DispatchPacketFunc<_Owner, GD_REQ_GAME_FINISH, _Args&&...>
   };

   if ((PacketTypes::GD_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::GD_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::GD_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

