#pragma pack(pop)

inline bool ValidatePacketGM(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::GM_PACKET_END - PacketTypes::GM_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<GM>,
       &ValidatePacketFunc<GM_GAME_SERVER_READY>,
       &ValidatePacketFunc<GM_ACK_GAME_LOAD>,
       &ValidatePacketFunc<GM_ACK_GAME_CREATE>,
       &ValidatePacketFunc<GM_ACK_GAME_USER_ENTER>,
       &ValidatePacketFunc<GM_ACK_GAME_USER_LEAVE>
   };

   if ((PacketTypes::GM_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::GM_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::GM_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketGM(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::GM_PACKET_END - PacketTypes::GM_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, GM, _Args&&...>,
       &DispatchPacketFunc<_Owner, GM_GAME_SERVER_READY, _Args&&...>,
       &DispatchPacketFunc<_Owner, GM_ACK_GAME_LOAD, _Args&&...>,
       &DispatchPacketFunc<_Owner, GM_ACK_GAME_CREATE, _Args&&...>,
       &DispatchPacketFunc<_Owner, GM_ACK_GAME_USER_ENTER, _Args&&...>,
       &DispatchPacketFunc<_Owner, GM_ACK_GAME_USER_LEAVE, _Args&&...>
   };

   if ((PacketTypes::GM_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::GM_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::GM_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

