#pragma pack(pop)

inline bool ValidatePacketMG(const Packet& rp)
{
   static constexpr bool(*ValidatePacketFuncArray[PacketTypes::MG_PACKET_END - PacketTypes::MG_PACKET_START - 1])(const Packet&) =
   {
       &ValidatePacketFunc<MG>,
       &ValidatePacketFunc<MG_REQ_GAME_LOAD>,
       &ValidatePacketFunc<MG_REQ_GAME_CREATE>,
       &ValidatePacketFunc<MG_REQ_GAME_USER_ENTER>,
       &ValidatePacketFunc<MG_REQ_GAME_USER_LEAVE>
   };

   if ((PacketTypes::MG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::MG_PACKET_END <= rp.GetPacketType()))
   {
       return true;
   }

   return ValidatePacketFuncArray[rp.GetPacketType() - PacketTypes::MG_PACKET_START - 1](rp);
}

template <typename _Owner, typename... _Args>
HandleResult DispatchPacketMG(uint64_t socket, _Owner* owner, NetworkPacket& rp, _Args&&... args)
{
   HandleResult handleResult = DispatchPacketCOMMON(socket, owner, rp, std::forward<_Args>(args)...);
   if (handleResult != HandleResult::NOT_EXISTS)
       return handleResult;
   
   static constexpr HandleResult(*DispatchPacketFuncArray[PacketTypes::MG_PACKET_END - PacketTypes::MG_PACKET_START - 1])(uint64_t, _Owner*, NetworkPacket&, _Args&&...) =
   {
       &DispatchPacketFunc<_Owner, MG, _Args&&...>,
       &DispatchPacketFunc<_Owner, MG_REQ_GAME_LOAD, _Args&&...>,
       &DispatchPacketFunc<_Owner, MG_REQ_GAME_CREATE, _Args&&...>,
       &DispatchPacketFunc<_Owner, MG_REQ_GAME_USER_ENTER, _Args&&...>,
       &DispatchPacketFunc<_Owner, MG_REQ_GAME_USER_LEAVE, _Args&&...>
   };

   if ((PacketTypes::MG_PACKET_START >= rp.GetPacketType()) ||
       (PacketTypes::MG_PACKET_END <= rp.GetPacketType()))
   {
       return HandleResult::NOT_EXISTS;
   }

   if (auto func = DispatchPacketFuncArray[rp.GetPacketType() - PacketTypes::MG_PACKET_START - 1])
       return func(socket, owner, rp, std::forward<_Args>(args)...);
   else
       return HandleResult::NOT_EXISTS;
}

