#pragma once

#include "Packet/BasePacketAutoGenerated/PACKET_COMMON.h"


class Game final
{
private:
	PacketKeep<GAME> mData;
	UserIdList mUserIdList;

public:
	Game() = default;

	void SetGame(const GAME& game) { mData = game; }
	GAME& GetGame() { return **mData; }
	const GAME& GetGame() const { return **mData; }

	bool AddUser(UserId userId);
	bool RemoveUser(UserId userId);

	const UserIdList& GetUserIdList() const { return mUserIdList; }
};
